//Mostra Monstro
void mostraMonstro(){

	setlocale(LC_ALL,"C");

	int y = 110;
	
	gotoxy(11,y);
	printf("                         .-.");
	gotoxy(12,y);
	printf("                       .'   `.");
	gotoxy(13,y);
	printf("                       :%c %c   :", 248, 248);
	gotoxy(14,y);
	printf("                       : o    `.", 248, 248);
	gotoxy(15,y);
	printf("                      :         ``.");
	gotoxy(16,y);
	printf("                     :             `.");
	gotoxy(17,y);
	printf("                    :  :         .   `.");
	gotoxy(18,y);
	printf("                    :   :          ` . `.");
	gotoxy(19,y);
	printf("                     `.. :            `. ``;");
	gotoxy(20,y);
	printf("                        `:;             `:'");
	gotoxy(21,y);
	printf("                           :              `.");
	gotoxy(22,y);
	printf("                            `.              `.     .");
	gotoxy(23,y);
	printf("                              `'`'`'`---..,___`;.-'");

	setlocale(LC_ALL,"");

}
