//Faz as molduras do jogo
void fazMolduras () {
	int i;
	/* Moldura do ambiente */
	//Permite usar caracteres ASCII
	setlocale(LC_ALL, "C");

	textcolor(1);
	//Canto superior esquerdo
	gotoxy(0,0);
	printf("%c", 201);

	//Bordas horizontais
	for(i=1; i < 167; i++) {
		gotoxy(0,i);
		printf("%c", 205);
		gotoxy(40,i);
		printf("%c", 205);
		gotoxy(47,i);
		printf("%c", 205);
		
	}

	//Canto superior direito
	gotoxy(0,167);
	printf("%c", 187);

	//Bordas verticais
	for (i=1; i<47; i++) {
		gotoxy(i,0);
		printf("%c", 186);
		gotoxy(i,167);
		printf("%c", 186);
	}
	
	// Divisor esquerdo
		gotoxy(40,0);
		printf("%c", 204);
		
	//Divisor direito
		gotoxy(40,167);
		printf("%c", 185);

	//Canto inferior esquerdo
	gotoxy(47,0);
	printf("%c", 200);

	//Canto inferior direito
	gotoxy(47,167);
	printf("%c", 188);
	
	/* Moldura do nome */

	//Canto superior esquerdo
	gotoxy(1,2);
	printf("%c", 218);

	//Bordas horizontais
	for(i=3; i<25; i++) {
		gotoxy(1,i);
		printf("%c", 196);
		gotoxy(6,i);
		printf("%c", 196);
	}

	//Canto superior direito
	gotoxy(1,25);
	printf("%c", 191);

	//Bordas verticais
	for (i=2; i<6; i++) {
		gotoxy(i,2);
		printf("%c", 207);
		gotoxy(i,25);
		printf("%c", 207);
	}
	
	//Canto inferior esquerdo
	gotoxy(6,2);
	printf("%c", 192);

	//Canto inferior direito
	gotoxy(6,25);
	printf("%c", 217);
	
	setlocale(LC_ALL,"");
	
	textcolor(15);

}

